package com.upskill.java_3;

public class Inheritance {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i = 22;
int j = 87;
  if (i>j){
	System.out.println("The first number " + i + " is bigger than the second number" +j);  
  }
  else if (i<j){
	  System.out.println("The second number "+ j +" is bigger than the first number");
  }
  else {
	  System.out.println("Both numbers are equal");
  }
	}

}
